package stringFunctionsconcept;

public class Stringfunction2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String name="Gobi";
		String name2="Manchurian";
		System.out.println(name.concat(" ").concat(name2));
		String name3="World";
		System.out.println(name3.charAt(0));
		System.out.println(name.charAt(1));
		System.out.println(name2.indexOf('M'));
		name.substring(0);//substring will found out the sub of the string
		
		
		

	}

}
