package switchcase;

public class Switchcasetopic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Switch,case,break,default are should be in smallcase
		switch(10)
		 {
		case 1:System.out.println("Addition");
			break;
		case 2:System.out.println("subtraction");
		break;
		default:System.out.println("I am the default one");
		 }
		
	}

}
