package typecasting;

public class PrimitiveTypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//1st problem(int to doublle)
		double myweight=90;//widening(done implicitly)
		//Explicitly means forcefully and implicitly means 
		
		
		//2nd problem(converting double to int)
		int yourweight=(int) 67.12; //Narrowing(done explicitly)
		System.out.println(yourweight);
		
		//3rd problem
		//convert byte to short data type
		byte a1=20;
		
		

	}

}
